export default class Paddle{constructor(i){this.GameWidth=i.GameWidth,this.width=150,this.height=10,this.MaxSpeed=7,this.Speed=0,this.position={x:i.GameWidth/2-this.width/2,y:i.GameHeight-this.height-10}}moveLeft(){this.Speed=-this.MaxSpeed}moveRight(){this.Speed=this.MaxSpeed}stop(){this.Speed=0}draw(i){i.fillStyle="#15b79e",i.fillRect(this.position.x,this.position.y,this.width,this.height)}update(i){this.position.x+=this.Speed,this.position.x<0&&(this.position.x=0),this.position.x+this.width>this.GameWidth&&(this.position.x=this.GameWidth-this.width)}}